body.script:
   document.addEventListener('DOMContentLoaded', function() {
       const creditLink = document.getElementById('credit-link');
       const errorMessage = document.getElementById('error-message');
       const expectedHref = 'https://www.fredybastian.com/';
       const expectedText = 'Fredy Bastian';

       function checkCreditLink() {
           if (creditLink.href !== expectedHref || creditLink.textContent !== expectedText) {
               errorMessage.style.display = 'block';
           } else {
               errorMessage.style.display = 'none';
           }
       }

       checkCreditLink();
   });





const apiKey = 'xxxxxxxxxxxxxxxxx';
   const channelId = 'xxxxxxxxxxxxxxxxx';
   const videoContainer = document.getElementById('video-container');

   fetch(`https://www.googleapis.com/youtube/v3/search?key=${apiKey}&channelId=${channelId}&part=snippet,id&order=date&maxResults=20`)
       .then(response => response.json())
       .then(data => {
           const videos = data.items;
           videos.forEach(video => {
               const videoId = video.id.videoId;
               const videoTitle = video.snippet.title;
               const videoItem = document.createElement('div');
               videoItem.classList.add('video-item');
               videoItem.innerHTML = `

                   ${videoTitle}
               `;
               videoContainer.appendChild(videoItem);
           });
       })
       .catch(error => console.error('Error fetching YouTube API:', error));
